package ForLoop;

public class DEsigns {//numbered triangle

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int a=1;a<=5;a++)
		{
		int no=a;
		for(int b=1;b<=a;b++)
		{System.out.print("  "+no);
		no=no+5-b;
		}
		{
		System.out.println();
	}
	}
	}
	}