package ForLoop;

public class Design3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int a=1;a<=6;a++)
		{{
			for(int b=5;b>=a;b--)
				System.out.print(" ");
		}
		for (int c=1;c<=a;c++)
		{
			System.out.print("*");
		}
	System.out.println();
	}
	
	}
}
