package ForLoop;

public class Design4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for (int a=1;a<=6;a++)
	{
		for (int b=1;b<=a;b++)
		{
			System.out.print(" ");
		}
		for (int c=6;c>=a;c--)
		{
			System.out.print("*");
		}
		for (int d=5;d>=a;d--)
		{
			System.out.print("*");
		}
		System.out.println();

	}

	}

}
